import React, { Component } from 'react';
import EnergyForm from '../Utility/EnergyForm';
import TemperatureForm from '../Utility/TemperatureForm';
import './settings.css';
import {SettingsAlert} from '../Utility/Alert';

class Settings extends Component {
  constructor(props){
    super();
    this.state={
      showWarning:false,
      success:false
    }
  }

  showWarning=()=>{
    this.setState={
      showWarning:true
    }
  } 

  succeeded=()=>{
    this.setState({
      success:true
    })
  }

  failed=()=>{
    this.setState({
      success:false
    })
  }

  render() {
    return (
      <div>
        <div className='energy-usage-control'>
            <SettingsAlert success={this.state.success}/>
            <h2>Energy Usuage Alert Setting</h2>
            <EnergyForm />
            <h2>Temprature Setting</h2>
            <TemperatureForm roomName="kitchen" succeeded={this.succeeded} failed={this.failed}/>
            <TemperatureForm roomName="bedroom" />
            <TemperatureForm roomName="livingroom" />
            <TemperatureForm roomName="bathroom" />
        </div>
      </div>
    )
  }
}

export default Settings;
