import React, { Component } from "react";
// import { loadAllData } from "../Utility/DataHandling";
import {makeData}  from "../Utility/DataHandling";
import PlotChart from "./PlotChart";
import './linechart.css';

class LineChart extends Component {

  render() {
    const {width,height,title,data,range} = this.props;
    return (
      <div  className="LineChartComponent">
        <h3 style={{textAlign:"center"}}>{title}</h3>
        {data ? (
          <PlotChart
            data={data}
            width={width}
            height={height}
            xFn={d => d.date}
            yFn={d => d.value}
            yDomain={range}
            margin={{ top: 20, left: 40, bottom: 20, right: 20 }}
          />
        ) : (
          <p>Data Not found.</p>
        )}
      </div>
    );
  }
}
export default LineChart;
