import React, { Component } from 'react'
import LineChart from '../LineChart/LineChart';
import {connect} from 'react-redux';
import {makeData} from '../Utility/DataHandling';
import * as d3 from "d3";
import {
  changeBathroomTemp,
  changeLivingroomTemp,
  changeKitchenTemp,
  changeBedroomTemp,

  setLivingroomStatus,
  setKitchenStatus,
  setBathroomStatus,
  setBedroomStatus,

  setLivingroomTempData,
  setBedroomTempData,
  setBathroomTempData,
  setKitchenTempData,
} from '../store/actions/temperatureSettingAction';

class Room extends Component {
  constructor(props){
    super();
  }

  componentDidMount(){
    const {
      kitchenCurrentTemp,
      bedroomCurrentTemp,
      bathroomCurrentTemp,
      livingroomCurrentTemp,


      setLivingroomTempData,
      setBedroomTempData,
      setBathroomTempData,
      setKitchenTempData,


    } = this.props;

    //prefill data
    setLivingroomTempData(makeData(livingroomCurrentTemp,0.2));
    setBedroomTempData(makeData(bedroomCurrentTemp,0.2));
    setBathroomTempData(makeData(bathroomCurrentTemp,0.2));
    setKitchenTempData(makeData(kitchenCurrentTemp,0.2));
  }


  componentDidUpdate(){
    const{
      roomName,

      kitchenTemperature,
      livingroomTemperature,
      bathroomTemperature,
      bedroomTemperature,

      kitchenCurrentTemp,
      bedroomCurrentTemp,
      bathroomCurrentTemp,
      livingroomCurrentTemp,

      livingroomTempData,
      bedroomTempData,
      bathroomTempData,
      kitchenTempData,

      changeBathroomTemp, 
      changeLivingroomTemp,
      changeKitchenTemp,
      changeBedroomTemp
    } = this.props;

    const temp =(function(){
      if(roomName==="Kitchen")  return {kitchenTemperature,kitchenCurrentTemp};
      if(roomName==="Livingroom") return {livingroomTemperature,livingroomCurrentTemp};
      if(roomName==="Bedroom") return {bedroomTemperature, bedroomCurrentTemp};
      if(roomName==="Bathroom") return {bathroomTemperature, bathroomCurrentTemp};
    })();

    var {tempTarget, currentTemp} = temp;

    const tempDiff = tempTarget - currentTemp;

    //set room status: cooling/heating/idle
    const setStatus=setRoomStatus=>{
      if(tempDiff>0){
        setRoomStatus("heating");
      }else if(tempDiff <0){
        setRoomStatus("cooling");
      }else{
        setRoomStatus("idle")
      };
    }

    if(roomName==="Kitchen") setStatus(setKitchenStatus);
    if(roomName==="Bedroom") setStatus(setBedroomStatus);
    if(roomName==="Livingroom") setStatus(setLivingroomStatus);
    if(roomName==="Bathroom") setStatus(setBathroomStatus);

    if(kitchenTempData&&kitchenTempData.length>0){

      this.showTempInterval = setInterval(()=>{
        if(currentTemp === tempTarget){
          if(roomName==="Kitchen") {
            var kitchenData = typeof kitchenTempData.data ==="undefined"?kitchenTempData:kitchenTempData.data;
            console.log("kitchenTempData",kitchenTempData);
            console.log("bathroomTempData",bathroomTempData);
            kitchenData.push({date:new Date(),value:d3.randomNormal(currentTemp, 0.2)()});
            kitchenData.shift();
            setKitchenTempData(kitchenData);
          }
          if(roomName==="Bedroom") {

            var bedroomData = typeof bedroomTempData.data ==="undefined"?bedroomTempData:bedroomTempData.data;
            bedroomData.push({date:new Date(),value:d3.randomNormal(currentTemp, 0.2)()});
            bedroomData.shift();
            setBedroomTempData(bedroomData);
          }
          if(roomName==="Bathroom") {
            var bathroomData = typeof bathroomTempData.data ==="undefined"?bathroomTempData:bathroomTempData.data;
            bathroomData.push({date:new Date(),value:d3.randomNormal(currentTemp, 0.2)()});
            bathroomData.shift();
            setBathroomTempData(bathroomData);
          }
          if(roomName==="Livingroom") {
            var livingroomData = typeof livingroomTempData.data ==="undefined"?livingroomTempData:livingroomTempData.data;
            livingroomData.push({date:new Date(),value:d3.randomNormal(currentTemp, 0.2)()});
            livingroomData.shift();
            setLivingroomTempData(livingroomData);
          }
        }},1000)

      this.changeTempInterval = setInterval(()=>{
        if(currentTemp!==tempTarget){
          if(roomName==="Kitchen") {
            currentTemp=Math.round((currentTemp+tempDiff/10)*10)/10;
            changeKitchenTemp(currentTemp);
            var kitchenData = kitchenTempData;

            kitchenData.push({date:new Date(),value:d3.randomNormal(currentTemp, 0.2)()});
            kitchenData.shift();
            setKitchenTempData(kitchenData);
          }
          if(roomName==="Bedroom") {
            currentTemp=Math.round((currentTemp+tempDiff/10)*10)/10;
            changeBedroomTemp(currentTemp);

            var bedroomData = bedroomTempData;
            bedroomData.push({date:new Date(),value:d3.randomNormal(currentTemp, 0.2)()});
            bedroomData.shift();
            setBedroomTempData(bedroomData);
          }
          if(roomName==="Bathroom") {
            currentTemp=Math.round((currentTemp+tempDiff/10)*10)/10;
            changeBathroomTemp(currentTemp);
            var bathroomData = bathroomTempData;
            bathroomData.push({date:new Date(),value:d3.randomNormal(currentTemp, 0.2)()});
            bathroomData.shift();
            setBathroomTempData(bathroomData);
          }
          if(roomName==="Livingroom") {
            currentTemp=Math.round((currentTemp+tempDiff/10)*10)/10;
            changeLivingroomTemp(currentTemp);
            var livingroomData = livingroomTempData;
            livingroomData.push({date:new Date(),value:d3.randomNormal(currentTemp, 0.2)()});
            livingroomData.shift();
            setLivingroomTempData(livingroomData);
          }

          tempTarget = Math.round(tempTarget*10)/10;

          if(currentTemp === tempTarget){
            clearInterval(this.changeTempInterval);
          }
        }
      },1000)
    }
  }

  componentWillUnmount(){
    clearInterval(this.changeTempInterval);
    clearInterval(this.showTempInterval);
  }

  render() {

    const {
      roomName,
      type,
      kitchenTempData,
      bedroomTempData,
      livingroomTempData,
      bathroomTempData,
    } =this.props;


    var data =(function(){
      if(roomName==="Kitchen") return kitchenTempData.data;
      if(roomName==="Livingroom") return livingroomTempData.data;
      if(roomName==="Bedroom") return bedroomTempData.data;
      if(roomName==="Bathroom")return bathroomTempData.data;
    })(); 

    console.log('checkinng data...',data);

    var temperature = "Loading...";
    if(typeof data!=="undefined"){
      console.log("testing...",data[data.length-1]);
      console.log("testing...value",data[data.length-1].value);
      const temp = data[data.length-1].value

      temperature=Math.round(temp*100)/100;
    }
    return (
      <div className='room'>
          <h1>{roomName}</h1>
          <h5>Temperature: {temperature}°F </h5>
          <h6>Status:cooling</h6>

          <LineChart 
              type={type}
              width={600} 
              height={400}
              title={'Current Temperature'}
              data = {data}
              range={[65,90]}
          />
      </div>
    )
  }
}

const mapStateToProps=state=>{

  return{
    kitchenTemperature: state.temperature.kitchenTemperature,
    livingroomTemperature: state.temperature.livingroomTemperature,
    bedroomTemperature: state.temperature.bedroomTemperature,
    bathroomTemperature: state.temperature.bathroomTemperature,

    livingroomCurrentTemp:state.temperature.livingroomCurrentTemp,
    bedroomCurrentTemp:state.temperature.bedroomCurrentTemp,
    bathroomCurrentTemp:state.temperature.bathroomCurrentTemp,
    kitchenCurrentTemp:state.temperature.kitchenCurrentTemp,

    livingroomStatus:state.temperature.livingroomStatus,
    bedroomStatus:state.temperature.bedroomStatus,
    bathroomStatus:state.temperature.bathroomStatus,
    kitchenStatus:state.temperature.kitchenStatus,

    livingroomTempData:state.temperature.livingroomTempData,
    bedroomTempData:state.temperature.bedroomTempData,
    bathroomTempData:state.temperature.bathroomTempData,
    kitchenTempData:state.temperature.kitchenTempData,
  }
}

export default connect(mapStateToProps, {
  changeBathroomTemp, 
  changeLivingroomTemp,
  changeKitchenTemp,
  changeBedroomTemp,

  setLivingroomStatus,
  setKitchenStatus,
  setBathroomStatus,
  setBedroomStatus,

  setLivingroomTempData,
  setBedroomTempData,
  setBathroomTempData,
  setKitchenTempData,
})(Room);
