import React from 'react';
import { connect } from "react-redux";
import {setEnergyThreshold} from '../store/actions/energySettingAction';
import './temconfig.css';

// const onSubmit=()=>{
//     console.log("hello")
// }

const EnergyForm=({energyThreshold})=>{
    return(
        <form className='temperature-form' >
            <h3>Current Energy Alert Threshold: {energyThreshold} </h3>
            <label htmlFor="energy-usuage">Set energy alert threshold: </label>
            <input 
                type="text" 
                id="energy-usuage" 
                name="energy-usuage" 
                onChange={()=>console.log("hello")}
                placeholder="Enter New Threshold.."
            />
            <input type="submit" id="submit" onSubmit = {()=>console.log(this.input.value)} value="Submit"/>
        </form>
    )
}

const mapStateToProps=state=>{
    return{
        energyThreshold: state.energyUsageThreshold
    }
};

export default connect(mapStateToProps, {setEnergyThreshold})(EnergyForm);
