import React from 'react';
import './temconfig.css';

const onSubmit=()=>{
    console.log("hello")
}

const EnergyForm=({energyThreshold})=>{
    return(
        <form className='temperature-form' >
            <h3>Current Energy Alert Threshold: {energyThreshold} </h3>
            <label htmlFor="energy-usuage">Set energy alert threshold: </label>
            <input type="text" id="energy-usuage" name="energy-usuage" placeholder="Enter New Threshold.."/>
            <input type="submit" id="submit" onsubmit = {onSubmit} value="Submit"/>
        </form>
    )
}

const mapStateToProps=state=>{
    return{
        energyThreshold: this.state.energyUsageThreshold
    }
};

export default EnergyForm;
