import React, { Component } from 'react';
import * as d3 from 'd3';
import {Donut3D_transition, Donut3D_draw} from './Donut3D';

const salesData=[
	{label:"Basic", color:"#3366CC"},
	{label:"Plus", color:"#DC3912"},
	{label:"Lite", color:"#FF9900"},
	{label:"Elite", color:"#109618"},
	{label:"Delux", color:"#990099"}
];

const changeData=()=>{
    Donut3D_transition("salesDonut", randomData(), 130, 100, 30, 0.4);
    Donut3D_transition("quotesDonut", randomData(), 130, 100, 30, 0);
}

const randomData=()=>{
    return salesData.map(function(d){ 
        return {label:d.label, value:1000*Math.random(), color:d.color};});
}



export default class componentName extends Component {

    componentDidMount() {
        var svg = d3.select("body").append("svg").attr("width",700).attr("height",300);

        svg.append("g").attr("id","salesDonut");
        svg.append("g").attr("id","quotesDonut");

        Donut3D_draw("salesDonut", randomData(), 150, 150, 130, 100, 30, 0.4);
        Donut3D_draw("quotesDonut", randomData(), 450, 150, 130, 100, 30, 0);
    };

    render() {
      return (
        <div>
          <button onClick="changeData()">Change Data</button>
        </div>
      )
    }
  }
