import * as d3 from "d3";

const MakeData = (value,segma) => {
  const rnd = d3.randomNormal(value, segma);

  let data = d3
    .range(300)
    .map(i => {
      return { date: new Date(new Date().getTime() - 1000 * i), value: rnd() };
    })
    .reverse();

  return () => {
    data.push({ date: new Date(), value: rnd() });
    data.shift();

    return data;
  };
};

const md = MakeData();

export const loadAllData = (callback = () => {}) => {
  callback(md());
};
