import { SET_ENERGY_THRESHOLD } from '../actions/actionTypes';

const initialState = {
    energyUsageThreshold: 3.5
}

export default function (state=initialState, action){
    switch(action.type){
        case SET_ENERGY_THRESHOLD: {
            return[{
                ...state,
                energyUsageThreshold: action.payload
            }]
        }
        default:{
            return{
                state
            }
        }
    }
}
