import React, { Component } from 'react'
import LineChart from '../LineChart/LineChart';
import DonutChart from '../DonutChart/DonutChart';
import {makeData} from '../Utility/DataHandling';
import './summary.css';
import {SettingsAlert} from '../Utility/Alert';
import { connect } from 'react-redux';
import Room from '../Rooms/Room';


class Summary extends Component {
  constructor(props){
    super(props);
    this.state={
      data:{},
      showWarning:false,
      kitchenUsuage:0.67,
      bedroomUsuage:2.22,
      bathroomUsuage:0.44,
      livingroomUsuage:1.67
    }
  }


  componentWillMount(){
    this.setState({
      data:makeData(1.5,0.2)
    })

    this.loadData = setInterval(() => {
      const data = makeData(1.5,0.2);
      this.setState({
        data:data
      })
    }, 1000);
  }

  componentDidMount(){
    const {

      kitchenTemperature,
      livingroomTemperature,
      bathroomTemperature,
      bedroomTemperature,

      kitchenCurrentTemp,
      bedroomCurrentTemp,
      bathroomCurrentTemp,
      livingroomCurrentTemp,  
    } = this.props;

    const updateTemp =(tempTarget,currentTemp)=>{
      return{
        temp1: typeof tempTarget === "number"? tempTarget: tempTarget.temperature,
        temp2: typeof currentTemp === "number"? currentTemp: currentTemp.temperature
      }
    }

    console.log(updateTemp(kitchenTemperature,kitchenCurrentTemp));
    this.changeTempInterval = setInterval(()=>{

      if(currentTemp!==tempTarget){
        if(roomName==="Kitchen") {

          currentTemp=Math.round((currentTemp+tempDiff/10)*10)/10;
          changeKitchenTemp(currentTemp);
          var kitchenData = this.state.temperatureData;

          kitchenData.push({date:new Date(),value:d3.randomNormal(currentTemp, 0.2)()});
          kitchenData.shift();
          this.setState({
            temperatureData:kitchenData
          })
          console.log("this.state.kitchenData",this.state.kitchenData);
        }
        if(roomName==="Bedroom") {
          currentTemp=Math.round((currentTemp+tempDiff/10)*10)/10;
          changeBedroomTemp(currentTemp);
          var bedroomData = this.state.temperatureData;
          bedroomData.push({date:new Date(),value:d3.randomNormal(currentTemp, 0.2)()});
          bedroomData.shift();
          this.setState({
            temperatureData:bedroomData
          })
        }
        if(roomName==="Bathroom") {
          currentTemp=Math.round((currentTemp+tempDiff/10)*10)/10;
          changeBathroomTemp(currentTemp);
          var bathroomData = this.state.temperatureData;
          bathroomData.push({date:new Date(),value:d3.randomNormal(currentTemp, 0.2)()});
          bathroomData.shift();
          this.setState({
            temperatureData:bathroomData
          })
        }
        if(roomName==="Livingroom") {
          currentTemp=Math.round((currentTemp+tempDiff/10)*10)/10;
          changeLivingroomTemp(currentTemp);
          var livingroomData = this.state.temperatureData;
          livingroomData.push({date:new Date(),value:d3.randomNormal(currentTemp, 0.2)()});
          livingroomData.shift();
          this.setState({
            temperatureData:livingroomData
          })
        }

        tempTarget = Math.round(tempTarget*10)/10;

        if(currentTemp === tempTarget){
          clearInterval(this.changeTempInterval);
        }
      }
    },1000)

  }

  componentWillUnmount(){
    clearInterval(this.changeTempInterval);
  }


  render() {
    // const test = this.state.data
    // console.log(test[test.length-1].value);

    return (
      <div id='summary'>
            <SettingsAlert showWarning={this.state.showWarning}/>
            <div id='charts'>
              <div id="line-chart">
                <LineChart
                    width={800} 
                    height={400}
                    data = {this.state.data}
                    range={[0,3]}
                    title={'Overall Energy Consumption'}
                />
              </div>
              <div id='donut-chart'><DonutChart /></div>
            </div>

          <div id="status-summary">
            <h2 className='headline'>Temperature Status</h2>
            <Room roomName={"Kitchen"} type="temperature" showChart={false}/>
            <Room roomName={"Bedroom"} type="temperature" showChart={false}/>
            <Room roomName={"Livingroom"} type="temperature" showChart={false}/>
            <Room roomName={"Bathroom"} type="temperature" showChart={false}/>
          </div>

      </div>
    )
  }
}

const mapStateToProps=state=>{
  return{
    kitchenTemperature: state.temperature.kitchenTemperature,
    livingroomTemperature: state.temperature.livingroomTemperature,
    bedroomTemperature: state.temperature.bedroomTemperature,
    bathroomTemperature: state.temperature.bathroomTemperature,

    livingroomCurrentTemp:state.temperature.livingroomCurrentTemp,
    bedroomCurrentTemp:state.temperature.bedroomCurrentTemp,
    bathroomCurrentTemp:state.temperature.bathroomCurrentTemp,
    kitchenCurrentTemp:state.temperature.kitchenCurrentTemp,
  }
}

export default connect(mapStateToProps)(Summary);
