import React, { Component } from 'react';
// import LineChart from '../LineChart/LineChart';
// import SetTemperature from '../Utility/SetTemperature';
import Room from './Room';
import {connect} from 'react-redux';
import {makeData} from '../Utility/DataHandling';
import './rooms.css';
import {
  changeBathroomTemp,
  changeLivingroomTemp,
  changeKitchenTemp,
  changeBedroomTemp,

  setLivingroomTempData,
  setBedroomTempData,
  setBathroomTempData,
  setKitchenTempData,
} from '../store/actions/temperatureSettingAction';


class Rooms extends Component {
  constructor(props){
    super();
  }

  componentDidMount(){
    const {
      kitchenCurrentTemp,
      bedroomCurrentTemp,
      bathroomCurrentTemp,
      livingroomCurrentTemp,

      setLivingroomTempData,
      setBedroomTempData,
      setBathroomTempData,
      setKitchenTempData,


    } = this.props;

    //prefill data
    setLivingroomTempData(makeData(livingroomCurrentTemp,0.2));
    setBedroomTempData(makeData(bedroomCurrentTemp,0.2));
    setBathroomTempData(makeData(bathroomCurrentTemp,0.2));
    setKitchenTempData(makeData(kitchenCurrentTemp,0.2));

  }



  render() {
    return (
      <div>
        <div id='rooms'>
          <Room roomName={"Kitchen"} type="temperature"/>
          <Room roomName={"Bedroom"} type="temperature"/>
          <Room roomName={"Livingroom"} type="temperature"/>
          <Room roomName={"Bathroom"} type="temperature"/>
        </div>
      </div>
    )
  }
}

const mapStateToProps=state=>{

  return{

    livingroomCurrentTemp:state.temperature.livingroomCurrentTemp,
    bedroomCurrentTemp:state.temperature.bedroomCurrentTemp,
    bathroomCurrentTemp:state.temperature.bathroomCurrentTemp,
    kitchenCurrentTemp:state.temperature.kitchenCurrentTemp,

  }
}

export default connect(mapStateToProps, {
  changeBathroomTemp, 
  changeLivingroomTemp,
  changeKitchenTemp,
  changeBedroomTemp,

  setLivingroomTempData,
  setBedroomTempData,
  setBathroomTempData,
  setKitchenTempData,
})(Rooms);
