import React, { Component } from 'react'
import LineChart from '../LineChart/LineChart';
import {connect} from 'react-redux';
import {makeData} from '../Utility/DataHandling';
import * as d3 from "d3";
import {changeBathroomTemp, changeLivingroomTemp,changeKitchenTemp,changeBedroomTemp} from '../store/actions/temperatureSettingAction';

class Room extends Component {
  constructor(props){
    super(props);
    this.state={
      kitchenData:{},
      bedroomData:{},
      livingroomData:{},
      bathroomData:{},
      cooling:false,
      heating:false,
      idle:false
    }
  }

  componentWillMount(){
    const {kitchenCurrentTemp,
      bedroomCurrentTemp,
      bathroomCurrentTemp,
      livingroomCurrentTemp} = this.props;
    //prefill data
    this.setState({
      kitchenData:makeData(kitchenCurrentTemp,0.2),
      bedroomData:makeData(bedroomCurrentTemp,0.2),
      livingroomData:makeData(livingroomCurrentTemp,0.2),
      bathroomData:makeData(bathroomCurrentTemp,0.2),
    })
  }

  componentDidMount(){
    const {
      roomName, 
      kitchenTemperature,
      livingroomTemperature,
      bathroomTemperature,
      bedroomTemperature,

      kitchenCurrentTemp,
      bedroomCurrentTemp,
      bathroomCurrentTemp,
      livingroomCurrentTemp,

      changeBathroomTemp, 
      changeLivingroomTemp,
      changeKitchenTemp,
      changeBedroomTemp

    } = this.props;



    // console.log(kitchenTemperature);

    var tempTarget =(function(){
      if(roomName==="Kitchen") return kitchenTemperature;
      if(roomName==="Livingroom") return livingroomTemperature;
      if(roomName==="Bedroom") return bedroomTemperature;
      if(roomName==="Bathroom")return bathroomTemperature;
    })();
    // console.log(tempTarget);

    tempTarget = typeof tempTarget === "number"? tempTarget: tempTarget.temperature;

    var currentTemp =(function(){
      if(roomName==="Kitchen") return kitchenCurrentTemp;
      if(roomName==="Livingroom") return livingroomCurrentTemp;
      if(roomName==="Bedroom") return bedroomCurrentTemp;
      if(roomName==="Bathroom")return bathroomCurrentTemp;
    })();


    currentTemp = typeof currentTemp === "number"? currentTemp: currentTemp.temperature;
    // console.log('currentTemp',currentTemp);
    const tempDiff = tempTarget - currentTemp;

    // console.log(tempDiff);




    //set room status: cooling/heating/idle
    if(tempDiff>0){
      this.setState({heating: true, cooling:false, idle:false})
    }else if(tempDiff <0){
      this.setState({heating: false, cooling:true,idle:false})
    }else{
      this.setState({heating: false, cooling:false,idle:true})
    };


    console.log('state kitchen data',this.state.kitchenData);
    console.log('function test',d3.randomNormal(currentTemp, 0.2)());
    console.log('new kitchendata',Object.assign({},this.state.kitchenData, {date:new Date(),value:d3.randomNormal(currentTemp, 0.2)}));


    if(currentTemp === tempTarget){
      this.showTempInterval = setInterval(()=>{
        if(roomName==="Kitchen") {
          this.setState({
            kitchenData:Object.assign({},this.state.kitchenData, {date:new Date(),value:d3.randomNormal(currentTemp, 0.2)()})
          })
          console.log("test",this.state.kitchenData)
        }
        if(roomName==="Bedroom") {
          this.setState({
            bedroomData:makeData(currentTemp,0.2)
          })
        }
        if(roomName==="Bathroom") {
          this.setState({
            bathroomData:makeData(currentTemp,0.2)
          })
        }
        if(roomName==="Livingroom") {
          this.setState({
            livingroomData:makeData(currentTemp,0.2)
          })
        }
      },1000)
    }

    this.changeTempInterval = setInterval(()=>{
      if(roomName==="Kitchen") {
        currentTemp=Math.round((currentTemp+tempDiff/10)*10)/10;
        changeKitchenTemp(currentTemp);
        console.log('data',makeData(currentTemp,0.2));
        this.setState({
          kitchenData:makeData(currentTemp,0.2)
        })
      }
      if(roomName==="Livingroom") changeLivingroomTemp(currentTemp+tempDiff/10);
      if(roomName==="Bedroom") changeBedroomTemp(currentTemp+tempDiff/10);
      if(roomName==="Bathroom") changeBathroomTemp(currentTemp+tempDiff/10);

      tempTarget = Math.round(tempTarget*10)/10;
      if(currentTemp === tempTarget){
          clearInterval(this.changeTempInterval);
      }
    },1000)

  }

  componentWillUnmount(){
    clearInterval(this.changeTempInterval);
    clearInterval(this.showTempInterval);
  }




  render() {
    const {roomName} =this.props;
    const {
      kitchenData,
      bedroomData,
      livingroomData,
      bathroomData,
      cooling,
      heating,
      idle
    } = this.state;
    var data =(function(){
      if(roomName==="Kitchen") return kitchenData;
      if(roomName==="Livingroom") return livingroomData;
      if(roomName==="Bedroom") return bedroomData;
      if(roomName==="Bathroom")return bathroomData;
    })();
    // console.log('kitchendata',kitchenData);

    return (
      <div className='room'>
          <h1>{roomName}</h1>
          <h5>Temperature: 26°F </h5>
          <h6>Status:cooling</h6>

          <LineChart 
              width={600} 
              height={400}
              title={'Current Temperature'}
              data = {data}
              range={[65,90]}
          />
      </div>
    )
  }
}

const mapStateToProps=state=>{
  return{
    kitchenTemperature: state.temperature.kitchenTemperature,
    livingroomTemperature: state.temperature.livingroomTemperature,
    bedroomTemperature: state.temperature.bedroomTemperature,
    bathroomTemperature: state.temperature.bathroomTemperature,

    livingroomCurrentTemp:state.temperature.livingroomCurrentTemp,
    bedroomCurrentTemp:state.temperature.bedroomCurrentTemp,
    bathroomCurrentTemp:state.temperature.bathroomCurrentTemp,
    kitchenCurrentTemp:state.temperature.kitchenCurrentTemp,
  }
}

export default connect(mapStateToProps, {
  changeBathroomTemp, 
  changeLivingroomTemp,
  changeKitchenTemp,
  changeBedroomTemp})(Room);
