import React, { Component } from 'react'
import LineChart from '../LineChart/LineChart';
import DonutChart from '../DonutChart/DonutChart';
import {makeData} from '../Utility/DataHandling';
import './summary.css';


export default class Summary extends Component {
  constructor(props){
    super(props);
    this.state={
      data:{}
    }
  }


  componentWillMount(){
    this.loadData = setInterval(() => {
      const data = makeData(2,0.5);
      this.setState({
        data:data
      })
    }, 1000);
  }

  componentWillUnmount(){
    clearInterval(this.loadData);
  }

  render() {
    // const data = makeData(1, 0.5);
    console.log(data);
    return (
      <div id='summary'>
            <div id='charts'>
              <div id="line-chart">
                <LineChart
                    width={800} 
                    height={400}
                    data = {this.state.data}
                    range={[0,5]}
                    title={'Overall Energy Consumption'}
                />
              </div>
              <div id='donut-chart'><DonutChart /></div>
            </div>

            <h2 className='headline'>Temperature Status</h2>
            <div id='rooms'>
              <div className='room'>
                Kitchen
                <h5>Temperature: 26C</h5>
                <h6>Status:cooling</h6>
              </div>

              <div className='room'>
                Bedroom
