import React from 'react';
import { connect } from "react-redux";
import {setEnergyThreshold} from '../store/actions/energySettingAction';
import './temconfig.css';

const EnergyForm=({energyThreshold, setEnergyThreshold})=>{
    return(
        <form 
            className='temperature-form' 
            onSubmit = {
                (e)=>{e.preventDefault();
                setEnergyThreshold(e.target[0].value)}
            } 
        >
            <h3>Current Energy Alert Threshold: {energyThreshold} </h3>
            <label htmlFor="energy-usuage">Set energy alert threshold: </label>
            <input 
                type="text" 
                id="energy-usuage" 
                name="energy-usuage" 
                placeholder="Enter New Threshold.."
            />
            {/* <input 
                type="submit" 
                id="submit"
                value="Submit"
            /> */}
            <button type="submit">Submit</button>
        </form>
    )
}

const mapStateToProps=state=>{
    return{
        energyThreshold: state.energyUsageThreshold
    }
};

export default connect(mapStateToProps, {setEnergyThreshold})(EnergyForm);
