import React from 'react';
import { connect } from "react-redux";
import {setBedroomTemp, setBathroomTemp,setKitchenTemp,setLivingroomTemp} from '../store/actions/temperatureSettingAction';
import './temconfig.css';


const EnergyForm=(
        roomName, {
        bathroomTemperature,
        livingroomTemperature,
        bedroomTemperature,
        kitchenTemperature,
        setBedroomTemp, 
        setBathroomTemp,
        setKitchenTemp,
        setLivingroomTemp}
        )=>{

    const room = roomName.roomName;
    const temp = ()=>{
        console.log(room)
        if(room==="kitchen") return kitchenTemperature;
        else if(room==="livingroom") return livingroomTemperature;
        else if(room==="bedroom") return bedroomTemperature;
        else return bathroomTemperature;
    };
    console.log(temp());

    return(
        <form 
            className='temperature-form' 
            onSubmit={
                (e)=>{
                    e.preventDefault();
                    switch(room){
                        case "kitchen":{
                            setKitchenTemp(e.target[0].value);
                            break;
                        }
                        case "livingroom":{
                            setLivingroomTemp(e.target[0].value);
                            break;
                        }
                        case "bedroom":{
                            setBedroomTemp(e.target[0].value);
                            break;
                        }
                        case "bathroom":{
                            setBathroomTemp(e.target[0].value);
                            break;
                        }
                        default:{
                            break;
                        }
                    }
                }
        }>
            <h3>Current temperature in {room}: </h3>
            <h3>Temperature set as: {temp} </h3>
            <label htmlFor="temperature">Set Temperature: </label>
            <input type="text" id="temperature" name="temperature" placeholder="Enter New Temperature.."/>
            <input type="submit" id="submit" value="Submit"/>
        </form>
    )
}

const mapStateToProps=state=>{
    console.log('temp',state.temperature.bathroomTemperature);

    return{
        bathroomTemperature: state.temperature.bathroomTemperature,
        livingroomTemperature: state.temperature.livingroomTemperature,
        bedroomTemperature: state.temperature.bedroomTemperature,
        kitchenTemperature: state.temperature.kitchenTemperature
    }
};

export default connect( mapStateToProps,{setBedroomTemp, setBathroomTemp,setKitchenTemp,setLivingroomTemp})(EnergyForm);
