import React, { Component } from "react";
import PlotChart from "./PlotChart";
import './linechart.css';

class LineChart extends Component {

  render() {

    const {width,height,title,data,range,type} = this.props;
    var labelY=" ",labelX="Time";
    type==='temperature'?labelY="Temperature(°F)":labelY="Energy Usuage(KWh)";

    console.log("type",type);
    return (
      <div  className="LineChartComponent">
        <h3 style={{textAlign:"center"}}>{title}</h3>

        {data ? (
          <div>
            <p className='labelY'>{labelY}</p>
            <div id="line-chart">
              <PlotChart
                data={data}
                width={width}
                height={height}
                xFn={d => d.date}
                yFn={d => d.value}
                yDomain={range}
                margin={{ top: 20, left: 40, bottom: 20, right: 20 }}
              />
              <p className='labelX'>{labelX}</p>
            </div>

          </div>
        ) : (
          <p>Data Not found.</p>
        )}
      </div>
    );
  }
}
export default LineChart;
