import React, { Component } from 'react'
import { connect } from "react-redux";
import {setEnergyThreshold} from '../store/actions/energySettingAction';
import './utility.css';

class EnergyForm extends Component {
    constructor(props){
        super();
        this.state={
            error:""
        }
    }

    handleSubmit=(e)=>{
        e.preventDefault();
        if(typeof e.target.value =="number" && e.target.value>=1&& e.target.value<=10){
            console.log("working...")
            this.props.setEnergyThreshold(e.target[0].value)
        }
        else{
            this.setState({
                errror:"Invalid input. Please fix the errors before submit."
            })
        }
    }

    handleChange=(e)=>{
        if(typeof e.target.value !=="number"){
            this.setState({
                error:"Invalid input. Please enter a number."
            })
        }else if(e.target.value<1 || e.target.value>10){
            this.setState({
                error:"Please enter a number between 1 and 10"
            })
        }
    }


    render(){
    return(
        <form 
            className='energy-usage-form' 
            onSubmit = {this.handleSubmit} 
        >
            <h5>Current Energy Alert Threshold:{this.props.energyUsageThreshold} </h5>
            <label htmlFor="energy-usuage">Set alert threshold:</label>
            <input 
                type="text" 
                id="energy-usuage" 
                name="energy-usuage" 
                placeholder="Enter New Threshold"
                onChange={this.handleChange}
                required
            />
            <span style={{color: "red"}}>{this.state.error}</span>
            <input 
                type="submit" 
                id="submit"
                value="Submit"
            />

        </form>
    )
}
}

const mapStateToProps=state=>{
    return{
        energyUsageThreshold: state.energyusage.energyUsageThreshold
    }
};

export default connect(mapStateToProps, {setEnergyThreshold})(EnergyForm);
