import React from 'react';
import { connect } from "react-redux";
import {setEnergyThreshold} from '../store/actions/energySettingAction';
import './utility.css';

var error = "";
const EnergyForm=({energyUsageThreshold, setEnergyThreshold})=>{

    return(
        <form 
            className='energy-usage-form' 
            onSubmit = {
                (e)=>{
                    e.preventDefault();
                    console.log(e.target.value)
                        setEnergyThreshold(e.target[0].value)
                }
            }
        >
            <h5>Current Energy Alert Threshold:{energyUsageThreshold} </h5>
            <label htmlFor="energy-usuage">Set alert threshold:</label>
            <input 
                type="text" 
                id="energy-usuage" 
                name="energy-usuage" 
                placeholder="Enter New Threshold"
                min="1"
                max="10"
                required
            />
            <span style={{color:"red"}}>{error}</span>
            <input 
                type="submit" 
                id="submit"
                value="Submit"
            />

        </form>
    )
}

const mapStateToProps=state=>{
    return{
        energyUsageThreshold: state.energyusage.energyUsageThreshold
    }
};

export default connect(mapStateToProps, {setEnergyThreshold})(EnergyForm);
