import React, { Component } from "react";
// import { loadAllData } from "../Utility/DataHandling";
import {makeData}  from "../Utility/DataHandling";
import PlotChart from "./PlotChart";
import './linechart.css';

class LineChart extends Component {
  constructor(props) {
    super(props);

    this.state = {
      data: null,
      body_width: document.body.clientWidth
    };

    window.addEventListener("resize", this.resize().bind(this));
  }

  // componentWillMount() {
  //   this.load();
  // }

  resize() {
    let t;

    return event => {
      if (t !== false) {
        clearTimeout(t);
      }
      t = setTimeout(() => {
        const state = Object.assign(this.state, {
          body_width: document.body.clientWidth
        });
        this.setState(state);
      }, 100);
    };
  }

  // load() {
  //   loadAllData(this.loaded.bind(this));
  //   // loadAllData(2,0.5);
  //   setTimeout(() => {
  //     this.load();
  //   }, 1000);
  // }

  // loaded(data) {
  //   this.setState({ data: data });
  // }

  componentWillMount(){
    const data = makeData(2,0.5);
    console.log(data);
    this.setState({ data: data });

    this.loadData = setInterval(() => {
      const data = makeData(2,0.5);
      this.setState({ data: data })
    }, 1000);
  }

  componentWillUnmount(){
    clearInterval(this.loadData);
  }

  render() {
    const {width, height, title, range} = this.props;
    return (
      <div  className="LineChartComponent">
        <h3 style={{textAlign:"center"}}>{title}</h3>
        {this.state.data ? (
          <PlotChart

            data={this.state.data}
            width={width}
            height={height}
            xFn={d => d.date}
            yFn={d => d.value}
            yDomain={range}
            margin={{ top: 20, left: 40, bottom: 20, right: 20 }}
          />
        ) : (
          <p>Data Not found.</p>
        )}
      </div>
    );
  }
}
export default LineChart;
