import React from 'react';
import { connect } from "react-redux";
import {setBedroomTemp, setBathroomTemp,setKitchenTemp,setLivingroomTemp} from '../store/actions/temperatureSettingAction';
import './temconfig.css';


const TemperatureForm=(
        {
        roomName,
        bathroomTemperature,
        livingroomTemperature,
        bedroomTemperature,
        kitchenTemperature,
        setBedroomTemp, 
        setBathroomTemp,
        setKitchenTemp,
        setLivingroomTemp}
        )=>{

    const temp =(function(){
        if(roomName==="kitchen") return kitchenTemperature;
        else if(roomName==="livingroom") return livingroomTemperature;
        else if(roomName==="bedroom") return bedroomTemperature;
        else return bathroomTemperature;
    })();

    return(
        <form 
            className='temperature-form' 
            onSubmit={
                (e)=>{
                    e.preventDefault();
                    switch(roomName){
                        case "kitchen":{
                            setKitchenTemp(e.target[0].value);
                            break;
                        }
                        case "livingroom":{
                            setLivingroomTemp(e.target[0].value);
                            break;
                        }
                        case "bedroom":{
                            setBedroomTemp(e.target[0].value);
                            break;
                        }
                        case "bathroom":{
                            setBathroomTemp(e.target[0].value);
                            break;
                        }
                        default:{
                            break;
                        }
                    }
                }
        }>
            <h3>Current temperature in {roomName}: </h3>
            <h3>Temperature set as: {temp} </h3>
            <label htmlFor="temperature">Set Temperature: </label>
            <input type="text" id="temperature" name="temperature" placeholder="Enter New Temperature.."/>
            <input type="submit" id="submit" value="Submit"/>
        </form>
    )
}

const mapStateToProps=state=>{
    console.log(state.temperature.kitchenTemperature);

    return{
        bathroomTemperature: typeof state.temperature.bathroomTemperature.temperature==="undefined"?state.temperature.bathroomTemperature:state.temperature.bathroomTemperature.temperature,

        bedroomTemperature: typeof state.temperature.bedroomTemperature.temperature==="undefined"?state.temperature.bedroomTemperature:state.temperature.bedroomTemperature.temperature,

        kitchenTemperature: typeof state.temperature.kitchenTemperature.temperature==="undefined"?state.temperature.kitchenTemperature:state.temperature.kitchenTemperature.temperature,

        livingroomTemperature: typeof state.temperature.livingroomTemperature.temperature==="undefined"?state.temperature.livingroomTemperature:state.temperature.livingroomTemperature.temperature,

    }
};

export default connect( mapStateToProps,{setBedroomTemp, setBathroomTemp,setKitchenTemp,setLivingroomTemp})(TemperatureForm);
