import React, { Component } from 'react';
import EnergyForm from '../Utility/EnergyForm';
import TemperatureForm from '../Utility/TemperatureForm';
import './settings.css';
import {SettingsAlert} from '../Utility/Alert';

class Settings extends Component {
  constructor(props){
    super();
    this.state={
      showWarning:false,
      showFailureAlert:false,
      showSuccessAlert:false
    }
  }

  showWarning=()=>{
    this.setState={
      showWarning:true
    }
  } 

  showSuccessAlert=()=>{
    this.setState({
      showSuccessAlert:true
    })
  }

  showFailureAlert=()=>{
    this.setState({
      showFailureAlert:true
    })
  }

  render() {
    const {showFailureAlert, showSuccessAlert} = this.state;
    return (
      <div>
        <div className='energy-usage-control'>
            <SettingsAlert showSuccessAlert={showSuccessAlert} showFailureAlert={showFailureAlert}/>
            <h2>Energy Usuage Alert Setting</h2>
            <EnergyForm />
            <h2>Temprature Setting</h2>
            <TemperatureForm roomName="kitchen" showSuccessAlert={this.showSuccessAlert} showFailureAlert={this.showFailureAlert}/>
            <TemperatureForm roomName="bedroom" />
            <TemperatureForm roomName="livingroom" />
            <TemperatureForm roomName="bathroom" />
        </div>
      </div>
    )
  }
}

export default Settings;
